    private void SimularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SimularActionPerformed
        
        Simulador simular = new Simulador();
        simular.Reiniciar();
        ticktock.purge();
        
        p.setText("0");
        ls.setText("0");
        lq.setText("0");
        ws.setText("0");
        wq.setText("0");
        sim_lq.setText("0");
        sim_ls.setText("0");
        sim_wq.setText("0");
        sim_ws.setText("0");
        sim_p.setText("0");
        pep.setText("0");
        pels.setText("0");
        pelq.setText("0");
        pews.setText("0");
        pewq.setText("0");
        caja.setBackground(new Color(240, 240, 240));
        f1.setBackground(new Color(240, 240, 240));
        f2.setBackground(new Color(240, 240, 240));
        f3.setBackground(new Color(240, 240, 240));
        f4.setBackground(new Color(240, 240, 240));
        f5.setBackground(new Color(240, 240, 240));
        f6.setBackground(new Color(240, 240, 240));
        f7.setBackground(new Color(240, 240, 240));
        f8.setBackground(new Color(240, 240, 240));
        f9.setBackground(new Color(240, 240, 240));
        f10.setBackground(new Color(240, 240, 240));
        f11.setBackground(new Color(240, 240, 240));
        f12.setBackground(new Color(240, 240, 240));
        cola.setText("0");
        tem.setText("0");
        cli.setText("0");
        
        String l = lambda.getText();
        String mi = miu.getText();
        String t = tiempo.getText();
        String c = clientes.getText();
        
        String m = modulo.getText();
        String a = multipli.getText();
        String b = Incremento.getText();
        String x = semilla.getText();
        
        if (Double.parseDouble(l) <= Double.parseDouble(mi)) {
            if (!l.equals("") && !mi.equals("") && !t.equals("") && !c.equals("") && !m.equals("") && !a.equals("") && !b.equals("") && !x.equals("")) 
            {
                simulador.setLambda(Double.parseDouble(l));
                simulador.setMiu(Double.parseDouble(mi));
                simulador.setClientes(Integer.parseInt(c));
                simulador.setTiempo(Double.parseDouble(t));

                simulador.setA(Integer.parseInt(a));
                simulador.setB(Integer.parseInt(b));
                simulador.setM(Integer.parseInt(m));
                simulador.setX(Double.parseDouble(x));  

                simulador.simulation();

                p.setText("" + String.format("%.2f", simulador.p()));
                ls.setText("" + String.format("%.2f", simulador.ls()));
                lq.setText("" + String.format("%.2f", simulador.lq()));
                ws.setText("" + String.format("%.2f", simulador.ws()));
                wq.setText("" + String.format("%.2f", simulador.wq()));

                // Valores Simulados
                sim_lq.setText("" + String.format("%.2f", simulador.getSimulado_lq()));
                sim_ls.setText("" + String.format("%.2f", simulador.getSimulado_ls()));
                sim_wq.setText("" + String.format("%.2f",simulador.getSimulado_wq()));
                sim_ws.setText("" + String.format("%.2f", simulador.getSimulado_ws()));
                sim_p.setText(""+String.format("%.2f", simulador.getSimulado_rho()));

                // Calcular Margen de Error
                pep.setText(String.format("%.2f", Math.abs(((simulador.getSimulado_rho()- simulador.p()) / simulador.p()) * 100)) +"%");
                pels.setText(String.format("%.2f", Math.abs(((simulador.getSimulado_ls() - simulador.ls()) / simulador.ls()) * 100)) + "%");
                pelq.setText(String.format("%.2f", Math.abs(((simulador.getSimulado_lq() - simulador.lq()) / simulador.lq()) * 100)) + "%");
                pews.setText(String.format("%.2f", Math.abs(((simulador.getSimulado_ws() - simulador.ws()) / simulador.ws()) * 100)) + "%");
                pewq.setText(String.format("%.2f", Math.abs(((simulador.getSimulado_wq() - simulador.wq()) / simulador.wq()) * 100)) + "%");

                graficar();
            }
            else {
                JOptionPane.showMessageDialog(null, "Digite todos los campos para realizar la simulaci√≥n");
            }
        }
        else
        {
            JOptionPane.showMessageDialog(null, "Simulaci√≥n Inestable");
        }
        
    }//GEN-LAST:event_SimularActionPerformed

    /**
     * @param args the command line arguments
     */
    public void graficar() {
        try {
            TimerTask painting = new TimerTask() {
                @Override
                public void run() {
                    if (!simulador.getArrayList().isEmpty()) {
                        int encola = (int) simulador.getArrayList().get(0).getEn_cola();
                        int ensistema = (int) simulador.getArrayList().get(0).getEn_sistema();
                        cola.setText("" + encola);
                        tem.setText("" + simulador.getArrayList().get(0).getTiempo());
                        cli.setText("" + simulador.getArrayList().get(0).getCliente());
                        if (ensistema >= 1) {
                            caja.setBackground(new Color(251, 150, 120));
                        } else {
                            caja.setBackground(new Color(240, 240, 240));
                        }
                        if (encola >= 2) {
                            f1.setBackground(new Color(251, 150, 120));
                        } else {
                            f1.setBackground(new Color(240, 240, 240));
                        }
                        if (encola >= 3) {
                            f2.setBackground(new Color(251, 150, 120));
                        } else {
                            f2.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 4) {
                            f3.setBackground(new Color(251, 150, 120));
                        } else {
                            f3.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 5) {
                            f4.setBackground(new Color(251, 150, 120));
                        } else {
                            f4.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 6) {
                            f5.setBackground(new Color(251, 150, 120));
                        } else {
                            f5.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 7) {
                            f6.setBackground(new Color(251, 150, 120));
                        } else {
                            f6.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 8) {
                            f7.setBackground(new Color(251, 150, 120));
                        } else {
                            f7.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 9) {
                            f8.setBackground(new Color(251, 150, 120));
                        } else {
                            f8.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 10) {
                            f9.setBackground(new Color(251, 150, 120));
                        } else {
                            f9.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 11) {
                            f10.setBackground(new Color(251, 150, 120));
                        } else {
                            f10.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 12) {
                            f11.setBackground(new Color(251, 150, 120));
                        } else {
                            f11.setBackground(new Color(240, 240, 240));
                        }

                        if (encola >= 13) {
                            f12.setBackground(new Color(251, 150, 120));
                        } else {
                            f12.setBackground(new Color(240, 240, 240));
                        }
                        simulador.getArrayList().remove(0);
                    } else {
                        ticktock.cancel();
                        ticktock.purge();
                        cola.setText("" + 0);
                        tem.setText("" + 0);
                        cli.setText("" + 0);
                        JOptionPane.showMessageDialog(null, "La simulaci√≥n ha finalizado");
                    }
                }
            };
            ticktock.schedule(painting, 0, 3000);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Error de ejecuci√≥n");
        }
    }    
    
    
    
